@Composable
fun PlayingItem(id: String, title: String, thumnail: String, onClick: (VideoItem) -> Unit,onOpen: ()->Unit ) {

 Row(
 modifier = Modifier
 .fillMaxWidth()
 .padding(start = 10.dp)
 .clickable { onClick(VideoItem(id, title, thumnail)) },
 verticalAlignment = Alignment.CenterVertically
 ) {
 AsyncImage(
 model = ImageRequest.Builder(LocalContext.current)
 .data(thumnail)
 .crossfade(true)
 .build(),
 contentDescription = null,
 placeholder = painterResource(R.drawable.imageloader),
 contentScale = ContentScale.Crop,
 modifier = Modifier
 .width(100.dp)
 .aspectRatio(16f / 9f)
 .clip(RoundedCornerShape(8.dp))
 )

 Spacer(
 modifier = Modifier
 .width(10.dp)
 .height(82.dp)
 )

 Column(
 modifier = Modifier.weight(1f) // text ke liye jagah
 ) {
 Text(
 text = title,
 fontSize = 14.sp,
 color = MaterialTheme.colorScheme.onBackground,
 maxLines = 2,
 overflow = TextOverflow.Ellipsis
 )
 }


 Box(
 modifier = Modifier
 .padding(16.dp)
 ) {
 IconButton(
 onClick = {onOpen()}
 ) {
 Icon(
 Icons.Default.MoreVert,
 contentDescription = "More options",
 tint = MaterialTheme.colorScheme.onBackground
 )
 }
 }
 }
}



