@Composable
fun DownloadAudioButton(
 context: Context= LocalContext.current,
 audioUrl: String="",
 fileName: String="",
 viewItem : VideoItem
) {
 var showDialog by remember { mutableStateOf(false) }
 val scop = rememberCoroutineScope()

 // Button
 Row(
 modifier = Modifier
 .fillMaxWidth()
 .clickable { showDialog = true }
 .height(52.dp)
 .padding(5.dp),
 horizontalArrangement = Arrangement.Start,
 verticalAlignment = Alignment.CenterVertically
 ) {
 Icon(
 Icons.Default.Download, contentDescription = "Download",
 modifier = Modifier
 .padding(start = 5.dp)
 .size(24.dp)
 )
 Spacer(Modifier.width(16.dp))
 Text(
 "Download",
 fontSize = 17.sp,
 modifier = Modifier.weight(1f)
 )
 }

 // Confirmation Dialog
 if (showDialog) {
 AlertDialog(
 onDismissRequest = { showDialog = false },
 title = { Text("Download Audio?") },
 text = { Text("This audio will be saved in your Music folder") },
 confirmButton = {
 TextButton(onClick = {
 scop.launch {
 showDialog = false
 val audio = getAudioUrl(viewItem.videoId)
 downloadFile(context , audio , fileName)
 }

 }){
 Text("Submit", color = MaterialTheme.colorScheme.onBackground)
 }},
 dismissButton = {
 TextButton(onClick = { showDialog = false }) {
 Text("Cancel", color = MaterialTheme.colorScheme.onBackground)
 }
 }
 )
 }
}



